import React from 'react';

class FormikCloudForm extends React.Component {
  handleSubmit = () => {
    fetch('https://api.formik.com/submit/portfolio-site-form/portfolio-site-form', {
      method: 'POST',
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(this.state),
    })
  }

  handleInputChange = ({ target: { name, value } }) => {
    this.setState({
      [name]: value
    });
  }

  render() {
    return (
      <form className='form' onSubmit={this.handleSubmit}>

        <input type="text" placeholder='Name' name="name"  onChange={this.handleInputChange} />

        <input type="text" placeholderce='Email' name="email" onChange={this.handleInputChange} />

        <textarea type="text" placeholder='Message' name="Message" onChange={this.handleInputChange} />

        <button type="submit">Submit</button>
      </form>
    );
  }
}

export default FormikCloudForm;
